mapper.insert:
	INSERT INTO codegroup(
		groupUsedNY,
	    codeGroupCode,
	    cgName,
		cgNameEng,
        count,
        cgOrder,
        cgRegDate,
        cgDelNY
	)VALUES(

		#{groupUsedNY},
		#{codeGroupCode},
		#{cgName}
		#{cgNameEng}
		#{count}
		#{cgOrder}
		#{cgRegDate}
		#{cgDelNY}
		)

			SELECT last_insert_id()

mapper.update:
	UPDATE codegroup SET
		groupUsedNY = #{groupUsedNY}
	    codeGroupCode = #{codeGroupCode}
	    cgName = #{cgName}
		cgNameEng = #{cgNameEng}
        count = #{count}
        cgOrder = #{cgOrder}
        cgRegDate = #{cgRegDate}
        cgDelNY = #{cgDelNY}

	WHERE seq = #{seq}
;
